    vector<double> coeffs2 = {-6, 11, -6, 1};

    printf("Root of polynomial 1: %.2f\n", find_zero(coeffs1));
    printf("Root of polynomial 2: %.2f\n", find_zero(coeffs2));

    return 0;
}

