import java.util.*;
import java.lang.*;

class Solution {
    public String circularShift(int x, int shift) {
        String numStr = Integer.toString(x);
        int length = numStr.length();
        if (shift > length) {
            return new StringBuilder(numStr).reverse().toString();
        }
        int effectiveShift = shift % length;
        if (effectiveShift == 0) {
            return numStr;
        }
        String shiftedPart = numStr.substring(length - effectiveShift);
        String remainingPart = numStr.substring(0, length - effectiveShift);
        return shiftedPart + remainingPart;
    }
}

Explanation:
