import java.util.*;
import java.lang.*;

class Solution {
    public String matchParens(List<String> lst) {
        int open1 = 0, close1 = 0, open2 = 0, close2 = 0;

        for (char c : lst.get(0).toCharArray()) {
            if (c == '(') open1++;
            else close1++;
        }

        for (char c : lst.get(1).toCharArray()) {
            if (c == '(') open2++;
            else close2++;
        }

        int totalOpen = open1 + open2;
        int totalClose = close1 + close2;

        if (totalOpen != totalClose) {
            return "No";
        }

        if (canFormBalanced(lst.get(0), lst.get(1)) || canFormBalanced(lst.get(1), lst.get(0))) {
            return "Yes";
        } else {
            return "No";
        }
    }

    private boolean canFormBalanced(String first, String second) {
        int balance = 0;

        for (char c : first.toCharArray()) {
            if (c == '(') balance++;
            else balance--;
            if (balance < 0) return false;
        }

        for (char c : second.toCharArray()) {
            if (c == '(') balance++;
            else balance--;
            if (balance < 0) return false;
        }

        return balance == 0;
    }
}

