import java.util.*;

class Solution {
    public List<Integer> minPath(List<List<Integer>> grid, int k) {
        int n = grid.size();
        int[][] directions = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};
        List<Integer> result = null;

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                Queue<Node> queue = new LinkedList<>();
                queue.add(new Node(i, j, new ArrayList<>(Collections.singletonList(grid.get(i).get(j)))));

                for (int step = 1; step < k; step++) {
                    int size = queue.size();
                    for (int s = 0; s < size; s++) {
                        Node current = queue.poll();
                        for (int[] dir : directions) {
                            int newX = current.x + dir[0];
                            int newY = current.y + dir[1];
                            if (newX >= 0 && newX < n && newY >= 0 && newY < n) {
                                List<Integer> newPath = new ArrayList<>(current.path);
                                newPath.add(grid.get(newX).get(newY));
                                queue.add(new Node(newX, newY, newPath));
                            }
                        }
                    }
                }

                while (!queue.isEmpty()) {
                    List<Integer> path = queue.poll().path;
                    if (result == null || comparePaths(path, result) < 0) {
                        result = path;
                    }
                }
            }
        }

        return result;
    }

    private int comparePaths(List<Integer> path1, List<Integer> path2) {
        for (int i = 0; i < path1.size(); i++) {
            if (path1.get(i) != path2.get(i)) {
                return path1.get(i) - path2.get(i);
            }
        }
        return 0;
    }

    class Node {
        int x, y;
        List<Integer> path;

        Node(int x, int y, List<Integer> path) {
            this.x = x;
            this.y = y;
            this.path = path;
        }
    }
}

