import java.util.*;
import java.lang.*;

class Solution {
    public String fixSpaces(String text) {
        StringBuilder result = new StringBuilder();
        int n = text.length();
        int i = 0;

        while (i < n) {
            if (text.charAt(i) == ' ') {
                int start = i;
                while (i < n && text.charAt(i) == ' ') {
                    i++;
                }
                int end = i;
                int spaceCount = end - start;

                if (spaceCount > 2) {
                    result.append('-');
                } else {
                    for (int j = 0; j < spaceCount; j++) {
                        result.append('_');
                    }
                }
            } else {
                result.append(text.charAt(i));
                i++;
            }
        }

        return result.toString();
    }

