  const memo = {};

  const computeTri = (n) => {
    if (n in memo) return memo[n];

    let result;
    if (n === 1) {
      result = 3;
    } else if (n % 2 === 0) {
      result = 1 + n / 2;
    } else {
      result = computeTri(n - 1) + computeTri(n - 2) + computeTri(n + 1);
    }

    memo[n] = result;
    return result;
  };

  const sequence = [];
  for (let i = 0; i <= n; i++) {
    sequence.push(computeTri(i));
  }

  return sequence;
};

