    let mut largest_prime = None;

    for &num in &lst {
        if is_prime(num) {
            match largest_prime {
                Some(prime) => {
                    if num > prime {
                        largest_prime = Some(num);
                    }
                },
                None => largest_prime = Some(num),
            }
        }
    }

    if let Some(prime) = largest_prime {
        sum_of_digits(prime)
    } else {
        0
    }
}

