fn eat(number: i32, need: i32, remaining: i32) -> Vec<i32> {
    let total_eaten = number + need.min(remaining);
    let remaining_after_eating = remaining - need.min(remaining);
    vec![total_eaten, remaining_after_eating]
}

fn main() {
