[asy]
unitsize(1.5 cm);

pair A, B, C, D, E, F;

A = (0,2);
B = (0,0);
C = (3,0);
D = (3,2);
E = (3*B + 2*C)/5;
F = (2*D + C)/3;

draw(A--B--C--D--cycle);
draw(A--E--F--cycle);

label("$A$", A, NW);
label("$B$", B, SW);
