label("$O$",O,S);
label("$C$",C,S);
label("$A$",A,E);
label("$B$",B,E);
draw(A--O--B--cycle,linewidth(0.7));
draw(C--B,linewidth(0.7));
draw(Circle(O,1),linewidth(0.7));
